INSERT INTO `vanban` (`id_vb`, `loaivb`, `noidung`) VALUES
(1, 'Giới thiệu', '<div style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Montserrat, sans-serif; margin: 0px auto; max-width: 1230px; padding: 0px;\"><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; font-weight: bolder;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">là hệ thống đặt vé máy bay, khách sạn, tour du lịch, tour tự túc, tour trải nghiệm, dịch vụ visa,... trực tuyến lớn nhất Việt Nam, cam kết mang lại chất lượng dịch vụ tốt nhất cùng mức giá rẻ nhất, và đội ngũ chuyên nghiệp hỗ trợ tận tình cho khách hàng.</font></span></div><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; font-weight: bolder;\"><br style=\"box-sizing: inherit;\"></span></div><div style=\"box-sizing: inherit; text-align: center;\"><img src=\"https://cdn.tripu.vn/vietravel/StaticPage/7bc04324-a8ae-44a0-999b-0dfe492aa8ac\" style=\"box-sizing: inherit; border-style: none; width: 1230px;\"><span style=\"box-sizing: inherit; font-weight: bolder;\"><br style=\"box-sizing: inherit;\"></span></div><div style=\"box-sizing: inherit; text-align: center;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; text-align: justify;\">Được thành lập từ năm 2008 dưới sự đầu tư - cố vấn bởi Vietravel và hoạt động theo mô hình sàn giao dịch thương mại điện tử. Kể từ 26/11/2019, Công ty TNHH MTV Dịch vụ Du lịch Tripu chính thức làm nóng thị trường du lịch trực tuyến đầy sôi động bằng Siêu ứng dụng TripU.&nbsp;</div><div style=\"box-sizing: inherit; text-align: justify;\">Đội ngũ nhiệt huyết của chúng tôi luôn không ngừng nỗ lực để mang lại nụ cười trên mọi hành trình của khách hàng, tái định nghĩa du lịch trong thời đại công nghệ số.</div><div style=\"box-sizing: inherit; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; text-align: center;\"><img src=\"https://cdn.tripu.vn/vietravel/StaticPage/14938ddf-763a-4bcf-9a9c-6ff355d281a5\" style=\"box-sizing: inherit; border-style: none; width: 1230px;\"><br style=\"box-sizing: inherit;\"></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><br style=\"box-sizing: inherit;\"></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><font color=\"#ff8040\" style=\"box-sizing: inherit;\">Khách sạn</font></h3><div style=\"box-sizing: inherit; text-align: justify;\">Hệ thống hơn 2.000.000&nbsp;<a href=\"https://tripu.vn/vi/hotels\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">khách sạn</a>&nbsp;sẵn sàng cho bạn lựa chọn. Chỉ cần vài cú nhấp, bạn sẽ có một căn phòng hào hảo theo đúng sở thích và yêu cầu.&nbsp;</div><div style=\"box-sizing: inherit; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">Chuyến bay</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Hơn 100.000 chuyến bay đến từ các hãng hàng không đối tác. Tích hợp so sánh giá và điều kiện đi kèm dễ dàng giúp việc booking&nbsp;</span><a href=\"https://tripu.vn/vi/flights\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">vé máy bay</a><span style=\"box-sizing: inherit; background-color: transparent;\">&nbsp;của bạn thuận tiện và giá cả phù hợp hơn.</span></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8040\" style=\"box-sizing: inherit;\">Tour trọn gói</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Đa dạng các&nbsp;</span><a href=\"https://tripu.vn/vi/etour\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">tour</a><span style=\"box-sizing: inherit; background-color: transparent;\">&nbsp;trọn gói từ chuyên nghiệp đến tiết kiệm được cung cấp bởi các kênh phân phối sản phẩm du lịch/dịch vụ hàng đầu.</span></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">Tour độc quyền</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Tour trải nghiệm, tour tự túc, tour thiết kế theo yêu cầu dành cho dân đam mê xê dịch tự do, thích khám phá Thế giới theo cách riêng của mình.</span><br style=\"box-sizing: inherit;\"></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8040\" style=\"box-sizing: inherit;\">Dịch vụ visa</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Hơn 25&nbsp;</span><a href=\"https://tripu.vn/vi/groundservices/visa\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">dịch vụ thị thực</a><span style=\"box-sizing: inherit; background-color: transparent;\">&nbsp;Á- Âu-Mỹ được tư vấn hỗ trợ tại TripU dưới hình thức offline lẫn online, tỷ lệ “đảm bảo” cao.</span><br style=\"box-sizing: inherit;\"></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">Dịch vụ du lịch lẻ khác</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Tất tần tật các thứ bạn cần khi đi du lịch, TripU có tất (Sim, card, wifi, vé vui chơi giải trí...)</span><br style=\"box-sizing: inherit;\"></div><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h3><h3 style=\"box-sizing: inherit; line-height: 32.12px; font-size: 2.92rem; margin: 1.94667rem 0px 1.168rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8040\" style=\"box-sizing: inherit;\">Blog du lịch</font></span></h3><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">Hội tụ nhiều góc ảnh, thước phim, câu chuyện, kinh nghiệm,...truyền cảm hứng cho bạn đi đây đó hoặc khám phá thêm nhiều điều thú vị.</span><br style=\"box-sizing: inherit;\"></div><h2 style=\"box-sizing: inherit; line-height: 39.16px; font-size: 3.56rem; margin: 2.37333rem 0px 1.424rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><br style=\"box-sizing: inherit;\"></span></h2><h2 style=\"box-sizing: inherit; line-height: 39.16px; font-size: 3.56rem; margin: 2.37333rem 0px 1.424rem; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">Tại sao bạn nên lựa chọn dịch vụ của TripU?</font></span></h2><div style=\"box-sizing: inherit; text-align: justify;\"><span style=\"box-sizing: inherit; background-color: transparent;\">- Tư vấn hỗ trợ 24/7, bất kể ngôn ngữ (tiếng Anh hay tiếng Việt)</span></div><div style=\"box-sizing: inherit; text-align: justify;\">- Website và App đa năng, công nghệ 4.0 dễ dàng sử dụng.</div><div style=\"box-sizing: inherit; text-align: justify;\">- Đối tác uy tín &nbsp;cung cấp các dòng các sản phẩm giá tốt nhất</div><div style=\"box-sizing: inherit; text-align: justify;\">- Nhiều chương trình ưu đãi độc quyền dành cho thành viên.</div><div style=\"box-sizing: inherit; text-align: justify;\">- Thanh toán dễ dàng, đa dạng hình thức (tiền mặt, thẻ tín dụng/visa,...và cả VÍ ĐIỆN TỬ).&nbsp;</div><div style=\"box-sizing: inherit; text-align: justify;\"><div style=\"box-sizing: inherit;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\"><span style=\"box-sizing: inherit; font-weight: bolder;\">Tải app TripU - Nhận ngay ưu đãi</span></font></div><div style=\"box-sizing: inherit;\">Android:&nbsp;<a href=\"https://bit.ly/35F2Twt\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">https://bit.ly/35F2Twt</a></div><div style=\"box-sizing: inherit;\"><span style=\"box-sizing: inherit; background-color: transparent;\">IOS:&nbsp;</span><a href=\"https://apple.co/2KZ8wgW\" target=\"_blank\" style=\"box-sizing: inherit; color: rgb(3, 155, 229);\">https://apple.co/2KZ8wgW</a><br style=\"box-sizing: inherit;\"></div></div><div style=\"box-sizing: inherit; text-align: center;\"><span style=\"box-sizing: inherit; font-weight: bolder;\"><font color=\"#ff8000\" style=\"box-sizing: inherit;\">“VUI TRỌN CHUYẾN, CHẤT NỤ CƯỜI”</font></span></div><div style=\"box-sizing: inherit; text-align: center;\">TripU luôn không ngừng nỗ lực và hoàn thiện để mọi trải nghiệm với TripU đều sẽ là niềm vui và sự hài lòng tuyệt đối.</div><div style=\"box-sizing: inherit;\"></div></div><div class=\"dl-div-map-full\" style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Montserrat, sans-serif;\"><div class=\"dl-tripu-location\" style=\"box-sizing: inherit; font-size: 36px; text-align: center; width: 1361px; padding: 30px 0px;\">TripU đã có mặt tại</div><div class=\"dl-map-full container\" id=\"tripu_locaiton\" style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; max-width: 1230px; padding: 0px; height: 400px; position: relative; overflow: hidden;\"><div style=\"box-sizing: inherit; height: 400px; width: 1230px; position: absolute; top: 0px; left: 0px; background-color: rgb(229, 227, 223);\"><div class=\"gm-style\" style=\"box-sizing: inherit; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 11px; line-height: normal; font-family: Roboto, Arial, sans-serif; position: absolute; z-index: 0; left: 0px; top: 0px; height: 400px; width: 1230px; padding: 0px; border-width: 0px; margin: 0px;\"><div tabindex=\"0\" style=\"box-sizing: inherit; position: absolute; z-index: 0; left: 0px; top: 0px; height: 400px; width: 1230px; padding: 0px; border-width: 0px; margin: 0px; cursor: url(&quot;https://maps.gstatic.com/mapfiles/openhand_8_8.cur&q